import React, { Component } from 'react';
import  { useState} from 'react';
import {Link } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
export const RegisterSecondStep=()=>{
    const [values,setValues]=useState({
        mail:"",
        password:""

    })


    const location = useLocation();
const data = location.state;
console.log(data);

const inputs=[
    {
        id:1,
        name:"first_name",
        type:"text",
        placeholder:"e.g. Jessica",
        label:"First name"
    },
    {
        id:2,
        name:"last_name",
        type:"text",
        placeholder:"e.g. Walton",
        label:"Last Name"
    },
    {
        id:3,
        name:"date+of_birth",
        type:"date",
        placeholder:"DD / MM / YYYY",
        label:"Date of birth"
    }
]

const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
}

    
    return(
        <div className='container'>
           <p id='darkSentence'>Great!</p> 
           <p id='graySentence'>Now your name</p> 
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         />

                    ))}
                    <p id="over18yo">You should be minium 18 years old</p>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <button className='nextStep'> Next step</button>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
