import React, { Component } from 'react';
import  { useState} from 'react';
import {Link } from "react-router-dom";
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
export const HomePage=()=>{
    const [values,setValues]=useState({
        mail:"",
        password:"",
        emailValid: false,
        passwordValid: false,
        formValid: false

    })

    
    
      function isValid(e) {
        
        const regex = /\d/;
        //console.log(e.target.name);
        if(e.target.name==="mail"){
            if(/\S+@monterail.com\S/.test(e.target.value)){
                document.getElementById("charakters").style.color = "green";
                setValues({...values,[values.emailValid]:true})
                return
        }    
      }
      if(e.target.name==="password"){
        const passwordInputValue = e.target.value;
        console.log(passwordInputValue);
        const digitsTest = /\d/;
        const lettersTest = /[a-zA-Z]/;
        const passwordLength = passwordInputValue.length;
        
        const digitsPassword =  digitsTest.test(passwordInputValue);
        const lettersPassword =  lettersTest .test(passwordInputValue);
        if(passwordLength>=8)setValues({...values,[values.passwordValid]:true})
        console.log(values.passwordValid);
        digitsPassword? document.getElementById("digit").style.color = "green" : document.getElementById("digit").style.color = "red";
        
        (passwordLength>=8)? document.getElementById("charakters").style.color = "green" : document.getElementById("charakters").style.color = "red";
        
        lettersPassword? document.getElementById("letter").style.color = "green" : document.getElementById("letter").style.color = "red";
           
        

    }
    
    
    
    }


   //console.log(isValidEmail(values.mail))

const inputs=[
    {
        id:1,
        name:"mail",
        type:"email",
        placeholder:"Something ending with monterail.com",
        label:"Mail",
        pattern:".+@monterail.com",
        required:true,
        
    },
    {
        id:2,
        name:"password",
        type:"password",
        placeholder:"Enter your password",
        label:"Password"
    }
]




const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
    isValid(e)
   
}



   //let data=inputs.value
   // console.log(data)
    return(
        <div className='container'>
          <div className='textHolder'>
          <p id='darkSentence'>Ahoy you!</p> 
           <p id='graySentence'>Care to register?</p> 
          </div>
          
           
           
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         onKeyDown={onKeyDown}
                         />

                    ))}
                    <p className='helper' id='charakters'>At least 8 characters</p>
                    <p className='helper' id='letter'>At least one letter</p>
                    <p className='helper' id='digit'>At least one digit</p>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <Link to="/SecondStep" state={values}>
                    <button className='nextStep' type="submit"  > Next step</button>
                    </Link>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
