import React, { Component } from 'react';
import  { useState} from 'react';
import {Link } from "react-router-dom";
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
export const HomePage=()=>{
    const [values,setValues]=useState({
        mail:"",
        password:"",
        emailValid: false,
        passwordValid: false,
        formValid: false

    })

const inputs=[
    {
        id:1,
        name:"mail",
        type:"email",
        placeholder:"Something ending with monterail.com",
        label:"Mail",
        pattern:".+@monterail.com",
        required:true,
    },
    {
        id:2,
        name:"password",
        type:"password",
        placeholder:"Enter your password",
        label:"Password"
    }
]

let validateField=(fieldName, values) =>{
    
    let emailValid = values.emailValid;
    let passwordValid = values.passwordValid;
  
    switch(fieldName) {
      case 'mail':
        emailValid = values.password.match(".+@monterail.com")
        console.log(emailValid)
        break;
      case 'password':
        passwordValid = values.password.length >= 6;
        break;
      default:
        break;
    }
    this.setValues({
                    emailValid: emailValid,
                    passwordValid: passwordValid
                  });
  }
  
 let validateForm=()=> {
    this.setState({formValid: this.state.emailValid && this.state.passwordValid})
  }




const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
    validateField(values.mail,values)
}
   //let data=inputs.value
   // console.log(data)
    return(
        <div className='container'>
           <p id='darkSentence'>Ahoy you!</p> 
           <p id='graySentence'>Care to register?</p> 
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         />

                    ))}
                    <p className='helper' id='charakters'>At least 8 characters</p>
                    <p className='helper' id='letter'>At least one letter</p>
                    <p className='helper' id='digit'>At least one digit</p>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <Link to="/SecondStep" state={values}>
                    <button className='nextStep' type="submit" disabled={!values.formValid} > Next step</button>
                    </Link>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
