import React, { Component } from 'react';
import  { useState} from 'react';
import {Link } from "react-router-dom";
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
export const HomePage=()=>{
    const [values,setValues]=useState({
        mail:"",
        password:"",
        emailValid: false,
        passwordValid: false,
        formValid: false

    })

    
    function isValidEmail(email) {
        //return /\S+@monterail.com/.test(email);
        if(/\S+@monterail.com/.test(email)){
        document.getElementById("charakters").style.color = "green";
        //setValues({...values,[values.emailValid]:true})
        //console.log(values.emailValid)
        }
      }
      function changeEmailValid(email) {
        return /\S+@monterail.com/.test(email);
      }
    
      function isValidPassword(pass) {
        //return /\S+@monterail.com/.test(email);
        if(/\S+@monterail.com/.test(pass)){
        document.getElementById("myP2").style.color = "blue";
        }
      }



   //console.log(isValidEmail(values.mail))

const inputs=[
    {
        id:1,
        name:"mail",
        type:"email",
        placeholder:"Something ending with monterail.com",
        label:"Mail",
        pattern:".+@monterail.com",
        required:true,
        
    },
    {
        id:2,
        name:"password",
        type:"password",
        placeholder:"Enter your password",
        label:"Password"
    }
]




const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
    isValidEmail(values.mail)
    isValidPassword(values.password)
   // console.log(values.emailValid)
}
   //let data=inputs.value
   // console.log(data)
    return(
        <div className='container'>
           <p id='darkSentence'>Ahoy you!</p> 
           <p id='graySentence'>Care to register?</p> 
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         />

                    ))}
                    <p className='helper' id='charakters'>At least 8 characters</p>
                    <p className='helper' id='letter'>At least one letter</p>
                    <p className='helper' id='digit'>At least one digit</p>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <Link to="/SecondStep" state={values}>
                    <button className='nextStep' type="submit" disabled={!values.formValid} > Next step</button>
                    </Link>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
