import React, { Component } from 'react';
import  { useState} from 'react';
import {Link } from "react-router-dom";
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
import PasswordInputField from "./PasswordInputField";
export const HomePage=()=>{
    const [values,setValues]=useState({
        mail:"",
        password:"",
        emailValid: false,
        passwordValid: false,
        formValid: false

    })

    const [passwordInput, setPasswordInput]= useState({
        password:'',
        
    })

    const handlePasswordChange =(evnt)=>{
        const passwordInputValue = evnt.target.value.trim();
        const passwordInputFieldName = evnt.target.name;
        const NewPasswordInput = {...passwordInput,[passwordInputFieldName]:passwordInputValue}
        setPasswordInput(NewPasswordInput);
        
    }

const inputs=[
    {
        id:1,
        name:"mail",
        type:"email",
        placeholder:"Something ending with monterail.com",
        label:"Mail",
        pattern:".+@monterail.com",
        required:true,
        
    },
    {
        id:2,
        name:"password",
        type:"password",
        placeholder:"Enter your password",
        label:"Password"
    }
]

const handleValidation= (evnt)=>{
    const passwordInputValue = evnt.target.value.trim();
    const passwordInputFieldName = evnt.target.name;
        //for password 
if(passwordInputFieldName==='password'){
    const uppercaseRegExp   = /(?=.*?[A-Z])/;
    const lowercaseRegExp   = /(?=.*?[a-z])/;
    const digitsRegExp      = /(?=.*?[0-9])/;
    const specialCharRegExp = /(?=.*?[#?!@$%^&*-])/;
    const minLengthRegExp   = /.{8,}/;
    const passwordLength =      passwordInputValue.length;
    const uppercasePassword =   uppercaseRegExp.test(passwordInputValue);
    const lowercasePassword =   lowercaseRegExp.test(passwordInputValue);
    const digitsPassword =      digitsRegExp.test(passwordInputValue);
    const specialCharPassword = specialCharRegExp.test(passwordInputValue);
    const minLengthPassword =   minLengthRegExp.test(passwordInputValue);
    let errMsg ="";
    if(passwordLength===0){
            console.log("Password is empty");
    }else if(!uppercasePassword){
            errMsg="At least one Uppercase";
    }else if(!lowercasePassword){
            errMsg="At least one Lowercase";
    }else if(!digitsPassword){
            errMsg="At least one digit";
    }else if(!specialCharPassword){
            errMsg="At least one Special Characters";
    }else if(!minLengthPassword){
            errMsg="At least minumum 8 characters";
    }else{
        errMsg="";
    }}}


const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
    
}
   //let data=inputs.value
   // console.log(data)
    return(
        <div className='container'>
           <p id='darkSentence'>Ahoy you!</p> 
           <p id='graySentence'>Care to register?</p> 
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         />

                    ))}
                    <PasswordInputField 
        handlePasswordChange={handlePasswordChange} 
        handleValidation={handleValidation} 
        passwordValue={passwordInput.password} 
        />
                    <p className='helper' id='charakters'>At least 8 characters</p>
                    <p className='helper' id='letter'>At least one letter</p>
                    <p className='helper' id='digit'>At least one digit</p>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <Link to="/SecondStep" state={values}>
                    <button className='nextStep' type="submit" disabled={!values.formValid} > Next step</button>
                    </Link>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
