import React, { Component } from 'react';
import  { useState} from 'react';
import { useLocation,Link } from 'react-router-dom';
import "./style.css"
import "./FormInput"
import FormInput from './FormInput';
export const RegisterSecondStep=()=>{
    const [values,setValues]=useState({
        first_name:"",
        last_name:"",
        date_of_birth:""

    })


    const location = useLocation();
const data = location.state;
//console.log(data);
let data1={
  mail:data.mail,
  first_name:values.first_name
}
const inputs=[
    {
        id:1,
        name:"first_name",
        type:"text",
        placeholder:"e.g. Jessica",
        label:"First name"
    },
    {
        id:2,
        name:"last_name",
        type:"text",
        placeholder:"e.g. Walton",
        label:"Last Name"
    },
    {
        id:3,
        name:"date_of_birth",
        type:"date",
        placeholder:"DD / MM / YYYY",
        label:"Date of birth"
    }
]
function getAge(dateString) {
    var today = new Date();
    var birthDate = new Date(dateString);
    var age = today.getFullYear() - birthDate.getFullYear();
    var m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
    }
    return age;
}

function isValid(e) {
    if(e.target.name==="date_of_birth"){
        //let age=getAge(e.target.value)
        //console.log(age)
        (getAge(e.target.value)>=18)? document.getElementById("over18yo").style.color = "green" : document.getElementById("over18yo").style.color = "red";
    }
    

}

const onChange=(e)=>{
    setValues({...values,[e.target.name]:e.target.value})
    isValid(e)
}

    
    return(
        <div className='container'>
           <p id='darkSentence'>Great!</p> 
           <p id='graySentence'>Now your name</p> 
            <div className='card' id='card'>
                
                
                
                <form>
                    {inputs.map((input)=>(
                        <FormInput key={input.id}
                         {...input} 
                        value={values[input.name]} 
                         onChange={onChange}
                         />

                    ))}
                    <p id="over18yo" className='helper'>You should be minium 18 years old</p>
                    <input type="checkbox" id="privacyPolicy"  />
                    <label for="checkbox" id="privacyPolicyLabel"> I accept <a id="privacyPolicyLink">Privacy Policy</a></label><br></br>
                    <div className='buttonDiv'>
                    <button className='logIn'>Log in instead</button>
                    <Link to="/Succes" state={data1}>
                    <button className='nextStep'> Next step</button>
                    </Link>
                    </div>
                    
                </form>
            </div>
        </div>
    )
}
